mapper.select:
SELECT ID
FROM USERINFO
WHERE UNO = ${b_uno}

mapper.update:
UPDATE BOARD
SET TITLE = #{title}, B_CONTENT = #{b_content}, U_DATE = SYSDATE
WHERE BNO = ${bno}

mapper.select:
SELECT COUNT(*)
FROM LIKE_HISTORY
WHERE 1=1
  AND BNO = #{bno}
  AND UNO = #{uno}

mapper.update:
UPDATE BOARD
SET LIKE_COUNT = (SELECT LIKE_COUNT FROM BOARD WHERE BNO = #{bno})+1
WHERE BNO = #{bno}
